mapper.select:
select count(id) from cw_base_house
status = 1
and villageCode =#{villageCode}

SELECT t1.id,t1.villageCode,t1.buildingNo,t1.unitNo,t1.houseNo,t1.floor,t1.peopleRelation,t1.houseType, t1.gisType,t1.lon,t1.lat,t1.createTime,t1.updateTime,t1.status, IFNULL(t1.address,CONCAT(t4.villageName,t1.buildingNo,'栋',t1.unitNo,'单元',t1.houseNo,'室')) AS address, t2.unitName,t3.buildingName,t4.villageName FROM cw_base_house t1 LEFT JOIN cw_base_unit t2 ON t1.unitNo = t2.unitNo AND t1.buildingNo = t2.buildingNo AND t2.villageCode = t1.villageCode LEFT JOIN cw_base_building t3 ON t1.buildingNo = t3.buildingNo AND t1.villageCode = t3.villageCode LEFT JOIN cw_base_village t4 ON t1.villageCode = t4.villageCode
t1.status = 1 AND t4.id IS NOT NULL AND t3.id IS NOT NULL AND t2.id IS NOT NULL AND t4.state =0 AND t3.isDelete = 0 AND t2.isDelete = 0
and t1.villageCode =#{villageCode}
and t1.buildingNo =#{buildingNo}
and t1.unitNo =#{unitNo}
and t1.houseNo =#{houseNo}
and t1.peopleRelation =#{peopleRelation}
and t1.houseType =#{houseType}
and t1.floor =#{floor}
AND FIND_IN_SET(t1.villageCode,#{sqlMap.dsf})

SELECT
*
FROM
cw_base_house


        AND id != #{id}


        AND villageCode = #{villageCode}


        AND buildingNo = #{buildingNo}


        AND unitNo = #{unitNo}


        AND floor = #{floor}


        AND houseNo = #{houseNo}

    AND status =1

LIMIT 0,1

SELECT
	*
FROM
	`cw_relation_peoplehouse`
WHERE
	villageCode = #{villageCode}
AND buildingNo = #{buildingNo}
AND unitNo = #{unitNo}
AND houseNo = #{houseNo}
AND credentialNo = #{credentialNo}
