mapper.select:
SELECT a.*, c.villageName, e.enumName AS companyTypeName, COUNT(p.companyCode) countPeople FROM cw_base_company a LEFT JOIN cw_base_village c ON a.villageCode = c.villageCode LEFT JOIN cw_base_enum e ON a.companyType = e.enumValue AND e.enumType = 'companyType' LEFT JOIN cw_base_company_people p on p.companyCode = a.companyCode AND p.villageCode = a.villageCode
AND a.companyName LIKE concat('%',#{companyName},'%')
AND a.companyType =#{companyType}
AND a.villageCode =#{villageCode}
AND a.isDelete =#{isDelete}
AND FIND_IN_SET(a.villageCode,#{villageCodes})
AND ${dsf}
GROUP BY a.companyCode

SELECT t2.enumType AS 'type', t2.enumName AS 'label', t2.enumValue AS 'desc', COUNT(id) AS 'number' FROM cw_base_company t1 LEFT JOIN cw_base_enum t2 ON t1.companyType = t2.enumValue AND t2.enumType = 'companyType'
t1.villageCode = #{villageCode}
GROUP BY t1.companyType;

SELECT t1.*,
t2.villageName
FROM cw_base_company t1
LEFT JOIN cw_base_village t2 ON t1.villageCode
= t2.villageCode


		t1.villageCode = #{villageCode}


		AND t1.companyName LIKE CONCAT('%', #{companyName}, '%')


		AND t1.companyType = #{companyType}


		AND t1.companySize = #{companySize}


		AND t1.importantFlag = #{importantFlag}


		AND t1.companyAdress = #{companyAdress}


		AND t1.leaderName LIKE CONCAT('%', #{leaderName}, '%')


		AND t1.leaderTel = #{leaderTel}


		AND t1.companyTel LIKE CONCAT('%', #{companyTel}, '%')


		AND ${sqlMap.dsf}
