mapper.select:
SELECT u.USER_ID AS userId, u.LOGIN_NAME AS loginName, u.PASSWORD AS password, u.`NAME` AS NAME, u.PHONE_NUMBER AS phoneNumber, u.WORK_CODE AS workCode, u.CREATED_USERID AS createUserId, u.CREATED_TIME AS createTime, o.ORG_CNAME AS orgName, u.SEX AS sex, u.BRITHDAY AS birthday, u.EMAIL AS email, u.ORG_ID AS orgId, u2.`LOGIN_NAME` AS createdName, u.BRITHDAY AS birthday FROM cw_ge_user u LEFT JOIN cw_ge_organization o ON u.ORG_ID = o.ORG_ID LEFT JOIN cw_ge_user u2 ON u.CREATED_USERID = u2.`USER_ID`
u.`STATUS` = 1
AND u.USER_ID = #{userId}
