mapper.select:
select ROLE_ID, ROLE_CNAME, ROLE_ENAME, STATUS, REMARK, INIT_FLAG, CREATED_TIME, CREATED_USERID, UPDATED_TIME, UPDATED_USERID, DELETED_TIME, DELETED_USERID from CW_GE_ROLE where 1 = 1
and ROLE_ID = #{roleId, jdbcType=VARCHAR}
and ROLE_CNAME like CONCAT('%', #{roleCname, jdbcType=VARCHAR}, '%')
and ROLE_ENAME like CONCAT('%', #{roleEname, jdbcType=VARCHAR}, '%')
and INIT_FLAG = #{initFlag, jdbcType=INTEGER}
and STATUS = #{status, jdbcType=INTEGER}
and STATUS in(0, 1)
order by CREATED_TIME desc

select ROLE_ID, ROLE_CNAME, ROLE_ENAME, STATUS, REMARK, INIT_FLAG, CREATED_TIME, CREATED_USERID, 
       UPDATED_TIME, UPDATED_USERID, DELETED_TIME, DELETED_USERID
  from CW_GE_ROLE
 where 1 = 1

 	and ROLE_ID = #{roleId, jdbcType=VARCHAR}



 	and ROLE_CNAME = #{roleCname, jdbcType=VARCHAR}



 	and ROLE_ENAME = #{roleEname, jdbcType=VARCHAR}



 	and INIT_FLAG = #{initFlag, jdbcType=INTEGER}




 		and STATUS = #{status, jdbcType=INTEGER}


 		and STATUS in(0, 1)

select ROLE_ID, ROLE_CNAME, ROLE_ENAME, STATUS, REMARK, INIT_FLAG, CREATED_TIME, CREATED_USERID, 
       UPDATED_TIME, UPDATED_USERID, DELETED_TIME, DELETED_USERID
  from CW_GE_ROLE
 where 1 = 1


		and ROLE_ID = #{roleId, jdbcType=VARCHAR}


		and ROLE_ID in

			#{item, jdbcType=VARCHAR}






		and STATUS = #{status, jdbcType=INTEGER}


		and STATUS in(0, 1)




 	and ROLE_CNAME = #{roleCname, jdbcType=VARCHAR}



 	and ROLE_ENAME = #{roleEname, jdbcType=VARCHAR}



 	and INIT_FLAG = #{initFlag, jdbcType=INTEGER}

mapper.update:
update CW_GE_ROLE
   set STATUS = -1,
   	   DELETED_TIME   = #{deleteTime, jdbcType=BIGINT},
       DELETED_USERID = #{deleteUserId, jdbcType=VARCHAR}



			ROLE_ID = #{roleId, jdbcType=VARCHAR}


			ROLE_ID in

				#{item, jdbcType=VARCHAR}



			1 != 1

mapper.delete:
delete from CW_GE_ROLE



			ROLE_ID = #{roleId, jdbcType=VARCHAR}


			ROLE_ID in

				#{item, jdbcType=VARCHAR}



			1 != 1
