            #{item, jdbcType=VARCHAR}





    and depart_id = #{departId, jdbcType=VARCHAR}



    and org_id = #{orgId, jdbcType=VARCHAR}



    and work_code = #{workCode, jdbcType=VARCHAR}



    and email = #{email, jdbcType=VARCHAR}



