and a.villageCode in #{villageCode}
AND FIND_IN_SET(a.villageCode,#{villageCodes})
and a.cameraType = #{cameraType}
and a.cameraName LIKE CONCAT('%', #{cameraName}, '%')
and ${sqlMap.dsf}
and a.cameraType in
#{item}
AND a.state = 0 AND b.state = 0

mapper.update:
UPDATE cw_base_camera
villageCode = #{villageCode}, cameraId = #{cameraId}, cameraIp = #{cameraIp}, cameraName = #{cameraName}, cameraPort = #{cameraPort}, password = #{password}, loginName = #{loginName}, streamSource = #{streamSource}, buildingNo = #{buildingNo}, installAdd = #{installAdd}, prducetBrand = #{prducetBrand}, productModel = #{productModel}, companyCode = #{companyCode}, thridType = #{thridType}, inOutFlag =#{inOutFlag}, cameraState =#{cameraState}, gisType = #{gisType}, lon = #{lon}, lat = #{lat}, alt = #{alt}, vehicleChannelNo = #{vehicleChannelNo}, camreaChannelID = #{camreaChannelID}, thirdCameraId = #{thirdCameraId}, standardCameraId = #{standardCameraId}, thirdCameraId = #{thirdCameraId}, updateTime = NOW()
WHERE id = #{id}

mapper.select:
SELECT a.*, ct.enumName AS cameraTypeDesc, cs.enumName AS cameraStateDesc, b.villageName FROM cw_base_camera a LEFT JOIN cw_base_village b ON a.villageCode = b.villageCode LEFT JOIN cw_base_enum ct ON ct.enumType = 'cameraType' AND a.cameraType = ct.enumValue LEFT JOIN cw_base_enum cs ON cs.enumType = 'CAMERA_STATE' AND a.cameraState = cs.enumValue
a.state = 0
AND a.villageCode = #{villageCode}
AND ${sqlMap.dsf}
AND FIND_IN_SET(a.villageCode,#{villageCodes})
AND a.cameraName LIKE CONCAT('%',#{cameraName},'%') AND a.prducetBrand LIKE CONCAT('%',#{prducetBrand},'%')
AND a.cameraType = #{cameraType} AND a.cameraState = #{cameraState}

