mapper.select:
SELECT t1.*, v.villageName, b.buildingName FROM cw_base_unit t1 LEFT JOIN cw_base_building b ON t1.buildingNo = b.buildingNo AND t1.`villageCode` = b.`villageCode` LEFT JOIN cw_base_village v ON t1.villageCode = v.villageCode
t1.villageCode = #{villageCode}
AND t1.buildingNo = #{buildingNo}
AND t1.unitName LIKE CONCAT('%',#{unitName},'%')
AND ${sqlString}
AND FIND_IN_SET(t1.villageCode,#{villageCodes})
AND b.isDelete = 0 AND t1.isDelete = 0 AND v.state = 0
ORDER BY t1.villageCode, t1.buildingNo, t1.unitNo

SELECT
MAX(CAST(unitNo AS SIGNED)) AS unitNo
FROM
cw_base_unit


        villageCode = #{villageCode}


        AND buildingNo = #{buildingNo}

SELECT u.*, b.buildingName, v.villageName FROM cw_base_unit u LEFT JOIN cw_base_building b ON u.buildingNo = b.buildingNo AND u.villageCode = b.villageCode LEFT JOIN cw_base_village v ON u.villageCode = v.villageCode
u.id = #{id}
AND u.isDelete = 0 AND b.isDelete = 0 AND v.state = 0

mapper.update:
UPDATE cw_base_unit
SET isDelete = #{isDelete}
where id = #{id}

mapper.select:
SELECT unitName, unitNo FROM cw_base_unit
isDelete = 0
AND villageCode = #{villageCode}
AND buildingNo = #{buildingNo}
