mapper.select:
SELECT v.villageName, h.buildingNo, h.houseNo, h.unitNo, h.id AS houseId, b.buildingName, u.unitName, v.villageCode, h.address FROM cw_relation_peoplehouse ph LEFT JOIN cw_base_house h ON ph.houseId = h.id LEFT JOIN cw_base_building b ON b.buildingNo = h.buildingNo AND b.villageCode = h.villageCode LEFT JOIN cw_base_unit u ON u.villageCode = h.villageCode AND u.buildingNo = h.buildingNo AND u.unitNo = h.unitNo LEFT JOIN cw_base_village v ON ph.villageCode = v.villageCode
ph.credentialNo = #{credentialNo}
AND ${sqlString}
AND h.`status` = 1 AND b.isDelete =0 AND u.isDelete = 0 AND v.state = 0

SELECT p.id, p.peopleId, p.credentialNo, p.peopleName, ph.peopleRelation, IFNULL(e.enumName,"其他") AS peopleRelationName, TIMESTAMPDIFF( YEAR, str_to_date(p.birthDate, '%Y-%m-%d'), CURDATE() ) AS age, p.residenceDetailAddres, p.facePic, p.label, p.nationalityCode, p.idCardPic, p.phoneNo, p.isLeave, p.nationCode, p.maritalStatusCode, p.genderCode, p.educationCode, enum.enumName AS peopleTypeName, enum1.enumName AS gender, enum2.enumName AS education, enum3.enumName AS maritalStatus, enum4.enumName AS nation, enum5.enumName AS nationality
FROM cw_relation_peoplehouse AS ph LEFT JOIN cw_base_people AS p ON ph.peopleId = p.id LEFT JOIN cw_base_enum AS e ON e.enumValue = ph.peopleRelation AND e.enumType = "houseRelation" LEFT JOIN cw_base_enum AS enum ON enum.enumValue = p.peopleType AND enum.enumType = "peopleType" LEFT JOIN cw_base_enum AS enum1 ON enum1.enumValue = p.genderCode AND enum1.enumType = "sex" LEFT JOIN cw_base_enum AS enum2 ON enum2.enumValue = p.educationCode AND enum2.enumType = "educationCode" LEFT JOIN cw_base_enum AS enum3 ON enum3.enumValue = p.maritalStatusCode AND enum3.enumType = "maritalStatusCode" LEFT JOIN cw_base_enum AS enum4 ON enum4.enumValue = p.nationCode AND enum4.enumType = "nation" LEFT JOIN cw_base_enum AS enum5 ON enum5.enumValue = p.nationalityCode AND enum5.enumType = "country"
AND ph.houseId = #{houseId}
AND ph.villageCode = #{villageCode}
AND ph.buildingNo = #{buildingNo}
AND ph.unitNo = #{unitNo}
AND ph.houseNo = #{houseNo}
AND ph.houseNo = #{houseNo}
AND p.isLeave = 0
ORDER BY p.isLeave, e.enumValue

SELECT plateNumber, inOutType, ( SELECT MAX(inOutTime) FROM `cw_vehicle_record` WHERE plateNumber = #{plateNo} AND inOutType = '1' ) AS inType, ( SELECT MAX(inOutTime) FROM `cw_vehicle_record` WHERE plateNumber = #{plateNo} AND inOutType = '2' ) AS outType FROM `cw_vehicle_record`
plateNumber = #{plateNo}
AND villageCode = #{villageCode}
ORDER BY inOutTime DESC

SELECT v.villageName, b.buildingName, u.unitName, h.houseNo FROM cw_base_village v LEFT JOIN cw_base_building b ON v.villageCode = b.villageCode LEFT JOIN cw_base_unit u ON b.villageCode = u.villageCode AND b.buildingNo = u.buildingNo LEFT JOIN cw_base_house h ON u.villageCode = h.villageCode AND u.buildingNo = h.buildingNo AND u.unitNo = h.unitNo
v.villageCode = #{villageCode}
AND b.buildingNo = #{buildingNo}
AND u.unitNo = #{unitNo}
AND h.houseNo = #{houseNo}
AND v.state = 0 AND b.isDelete = 0 AND u.isDelete = 0 AND h.`status` = 1

SELECT e.enumName FROM cw_base_enum e
e.enumValue = #{label}
AND e.enumType = "label"
