mapper.select:
SELECT t1.id, t1.plateNo, t1.carType, t1.carColor, e2.enumName AS carColorName, e.enumName AS carTypeName, t1.plateType, e1.enumName AS plateTypeName, p.peopleName AS registerName, t1.platePic, p.credentialNo, p.residenceDetailAddres AS adress, p.villageCode, v.villageName FROM cw_base_vehicle t1 LEFT JOIN cw_base_people p ON t1.peopleId = p.id LEFT JOIN cw_base_enum e ON e.enumValue = t1.carType AND e.enumType = "carType" LEFT JOIN cw_base_enum e1 ON e1.enumValue = t1.plateType AND e1.enumType = "plateType" LEFT JOIN cw_base_enum e2 ON e2.enumValue = t1.carColor AND e2.enumType = "carColor" LEFT JOIN cw_base_village v ON v.villageCode = p.villageCode
AND t1.plateNo = #{plateNo}
AND t1.carType = #{carType}
AND t1.plateType = #{plateType}
AND t1.carColor = #{carColor}
AND t1.villageCode = #{villageCode}
AND t1.registerName LIKE concat('%',#{registerName},'%')
AND FIND_IN_SET(t1.villageCode,#{villageCodes})
AND t1.isLeave = 0
ORDER BY t1.isLeave

SELECT t1.id, t1.villageCode, v.villageName, t1.inOutTime, t1.platePicUrl, t1.days, t1.status, e.enumName AS statusName FROM cw_vehicle_discovery t1 LEFT JOIN cw_base_village v ON t1.villageCode = v.villageCode LEFT JOIN cw_base_enum e ON t1.status = e.enumValue AND e.enumType = "state"
AND t1.plateNumber = #{plateNumber}
AND ${sqlString}
AND v.state = 0
ORDER BY t1.updateTime DESC LIMIT 0,1

SELECT t1.id, t1.villageCode, v.villageName, t1.inOutTime, t1.platePicUrl, t1.days, t1.status, e.enumName AS statusName FROM cw_vehicle_leave t1 LEFT JOIN cw_base_village v ON t1.villageCode = v.villageCode LEFT JOIN cw_base_enum e ON t1.status = e.enumValue AND e.enumType = "state"
AND t1.plateNumber = #{plateNumber}
AND ${sqlString}
AND v.state = 0
ORDER BY t1.updateTime DESC LIMIT 0,1

SELECT t1.id, t1.villageCode, v.villageName, t1.inOutTime, t1.platePicUrl, t1.days, t1.status, e.enumName AS statusName FROM cw_vehicle_retention t1 LEFT JOIN cw_base_village v ON t1.villageCode = v.villageCode LEFT JOIN cw_base_enum e ON t1.status = e.enumValue AND e.enumType = "state"
AND t1.plateNumber = #{plateNumber}
AND ${sqlString}
AND v.state = 0
ORDER BY t1.updateTime DESC

SELECT v.plateNo, v.plateType, e.enumName AS plateTypeName, v.carType, e1.enumName AS carTypeName, v.carColor, e2.enumName AS carColorName, v.platePic, p.residenceDetailAddres AS adress FROM cw_base_vehicle v LEFT JOIN cw_base_people p ON v.peopleId = p.id LEFT JOIN cw_relation_peoplehouse ph ON p.id = ph.peopleId LEFT JOIN cw_base_enum e ON v.plateType = e.enumValue AND e.enumType = "plateType" LEFT JOIN cw_base_enum e1 ON v.carType = e1.enumValue AND e1.enumType = "carType" LEFT JOIN cw_base_enum e2 ON v.carColor = e2.enumValue AND e2.enumType = "carColor"
AND ph.houseId = #{houseId}
AND v.villageCode = #{villageCode}
AND v.buildingNo = #{buildingNo}
AND v.unitNo = #{unitNo}
AND v.houseNo = #{houseNo}
AND v.isLeave = 0
ORDER BY v.isLeave

SELECT * FROM cw_base_vehicle
and plateNo = #{plateNo}
and id != #{id}
AND status = 0
LIMIT 0,1
