mapper.select:
SELECT * FROM `cw_vehicle_record`
plateNumber = #{plateNumber}
AND villageCode = #{villageCode}
ORDER BY inOutTime DESC

SELECT d.id, d.inOutTime, d.inOutType, e.enumName AS inOutTypeName, d.tollgateID, t1.tollgateName, d.villageCode, v.villageName, d.platePicUrl FROM cw_vehicle_record d LEFT JOIN cw_base_enum e ON e.enumValue = d.inOutType AND e.enumType = "inOutFlag" LEFT JOIN cw_base_village v ON d.villageCode = v.villageCode LEFT JOIN cw_vehicle_tollgate t1 ON d.tollgateID = t1.tollgateID AND d.villageCode = t1.villageCode
AND d.plateNumber = #{plateNumber}
AND ${sqlString}
ORDER BY d.inOutTime DESC

SELECT TO_DAYS(NOW()) - TO_DAYS(vr.inOutTime) AS days, vr.* FROM (SELECT MAX(id) AS id FROM cw_vehicle_record
leaveState = 0
AND villageCode = #{villageCode}
GROUP BY plateNumber) r LEFT JOIN cw_vehicle_record vr ON r.id = vr.id INNER JOIN cw_base_vehicle v ON vr.plateNumber = v.plateNo AND vr.villageCode = v.villageCode
vr.inOutType = 2 AND v.isLeave = 0
AND vr.inOutTime <= #{beginTime}

SELECT TO_DAYS(NOW())  - TO_DAYS(vr.inOutTime) AS days, vr.*
FROM (SELECT MAX(id) AS id FROM cw_vehicle_record 

		retenState = 0

			AND villageCode = #{villageCode}

		AND plateNumber NOT IN (SELECT plateNo FROM cw_base_vehicle


												villageCode = #{villageCode}

											AND isLeave = 0
										)

		GROUP BY plateNumber) r
LEFT JOIN cw_vehicle_record vr ON r.id = vr.id

	vr.inOutType = 1

		AND vr.inOutTime <= #{beginTime}

SELECT r.* FROM cw_vehicle_record r RIGHT JOIN ( SELECT MAX(id) AS id FROM cw_vehicle_record WHERE plateNumber NOT IN ( SELECT plateNo FROM cw_base_vehicle
villageCode = #{villageCode}
AND isLeave = 0
)
AND villageCode = #{villageCode}
AND inOutTime BETWEEN #{beginTime} AND #{endTime}
AND withState != 1 GROUP BY plateNumber ) b ON r.id = b.id

SELECT
	*
FROM
	cw_vehicle_record record


	record.villageCode = #{villageCode}


	AND record.plateNumber = #{plateNumber}


	AND record.inOutTime  BETWEEN #{beginTime} AND #{endTime}

AND withState != 1
GROUP BY
	DATE_FORMAT(inOutTime, "%y-%m-%d")

     	SELECT
			*
		FROM
			cw_vehicle_discovery


				villageCode = #{villageCode}


				AND plateNumber = #{plateNumber}

update.foreach:
UPDATE cw_vehicle_leave_detail
SET dayEnd = #{item.dayEnd},
days = #{item.days}
WHERE
	id = #{item.detailId}

mapper.select:
SELECT * FROM cw_vehicle_record
inOutTime BETWEEN #{inType} AND #{outType}
AND villageCode = #{villageCode}
GROUP BY plateNumber

     	SELECT
		r.*
		FROM
			cw_vehicle_record r
		RIGHT JOIN (
			SELECT
				MAX(id) AS id
			FROM
				cw_vehicle_record


					inOutTime BETWEEN #{inType}	AND #{outType}


					AND villageCode = #{villageCode}


					AND plateNumber = #{plateNumber}


		) m ON r.id = m.id

update.foreach:
UPDATE cw_vehicle_leave 
SET inOutTime = #{item.inOutTime},
status=3,
platePicUrl = #{item.platePicUrl},
plateNoPicUrl = #{item.plateNoPicUrl}

	plateNumber = #{item.plateNumber}
	AND status = 1
	AND villageCode = #{item.villageCode};


UPDATE cw_vehicle_retention 
SET inOutTime = #{item.inOutTime},
status=3,
platePicUrl = #{item.platePicUrl},
plateNoPicUrl = #{item.plateNoPicUrl}

	plateNumber = #{item.plateNumber}
	AND status = 1
	AND villageCode = #{item.villageCode};


UPDATE cw_base_vehicle
SET isLeave = 1

	plateNo = #{item.plateNumber}
	AND villageCode = #{item.villageCode}
