mapper.select:
SELECT COUNT(t.addPersonId) FROM cw_add_person_collect t INNER JOIN ( SELECT addPersonId, MAX(updateTime) AS updateTime FROM cw_add_person_collect t GROUP BY addPersonId ) tmp ON t.addPersonId = tmp.addPersonId AND t.updateTime = tmp.updateTime LEFT JOIN cw_add_person t1 ON t1.id = t.addPersonId
t1.state =1
AND t1.villageId = #{villageCode}
AND t.days >=#{days}

SELECT * FROM `cw_add_person`
villageId =#{villageCode}
ORDER BY days desc LIMIT 1,5

SELECT t1.captureId, t1.captureUrlFull, t1.panoramaUrlFull, t1.captureTime, t2.cameraName, t2.installAdd FROM cw_add_person_detail t1 LEFT JOIN cw_base_camera t2 on t1.deviceId = t2.cameraId
t1.addPersonId = #{addPersonId}
AND t1.captureTime >=#{dayBegin}
AND t1.captureTime <#{dayEnd}
ORDER BY t1.captureTime DESC

SELECT * FROM cw_add_person_detail 
WHERE addPersonId=#{addPersonId}
ORDER BY captureTime DESC LIMIT 1

SELECT t1.id, t1.capturePath, t1.panoramaPath, t1.peopleId, t1.deviceNo, t1.villageId AS villageCode, t1.regionCode, t1.state, t1.keyState, t1.excludeFlag, t1.lastCaptureTime, t.updateTime, t1.facePitch, t1.faceYaw, t1.faceRoll, t1.facex, t1.facey, t1.faceWidth, t1.faceHeight, t.days, t2.villageName, t2.address AS villageAddress, t3.peopleName, t3.residenceAddress, t3.credentialNo, e.enumName AS stateName FROM cw_add_person_collect t INNER JOIN ( SELECT temp.addPersonId, MAX(temp.updateTime) AS updateTime FROM ( SELECT addPersonId,updateTime FROM cw_add_person_collect WHERE days >= #{days} ) temp GROUP BY temp.addPersonId ) tmp ON t.addPersonId = tmp.addPersonId AND t.updateTime = tmp.updateTime LEFT JOIN cw_add_person t1 ON t1.id = t.addPersonId LEFT JOIN cw_base_village t2 ON t1.villageId = t2.villageCode LEFT JOIN cw_base_people t3 ON t1.peopleId = t3.peopleId LEFT JOIN cw_base_enum e ON e.enumValue = t1.state AND enumType ="state"
t1.id IS NOT NULL AND t1.state !=4
AND t3.peopleName LIKE CONCAT('%', #{condition}, '%') OR t3.credentialNo LIKE CONCAT('%', #{condition}, '%') OR t3.phoneNo LIKE CONCAT('%', #{condition}, '%')
AND t1.villageId = #{villageCode}
AND t1.state = #{state}
AND t.updateTime >= #{beginTime}
AND t.updateTime <= #{endTime}
AND FIND_IN_SET(t1.villageId,#{sqlMap.dsf})
ORDER BY t1.state ASC,t.updateTime DESC,t1.lastCaptureTime DESC

SELECT * FROM `cw_add_person`


		AND capturePath =#{capturePath}


		AND panoramaPath =#{panoramaPath}


		AND deviceNo =#{deviceNo}


		AND doorCode =#{doorCode}


		AND doorName =#{doorName}


		AND villageId =#{villageId}


		AND regionCode =#{regionCode}


		AND lastCaptureTime =#{lastCaptureTime}


LIMIT 0,1
