mapper.select:
SELECT COUNT(t1.id) FROM cw_base_people t1 LEFT JOIN cw_base_village t2 ON t1.villageCode = t2.villageCode
t1.isLeave = 0 AND t2.villageCode IS NOT NULL
AND t1.villageCode = #{villageCode}

SELECT cbe.enumValue AS "value", cbe.enumName AS "label", cbe.enumType AS "type", COUNT(1) AS "num" FROM cw_base_enum cbe LEFT JOIN cw_base_people cbp ON cbe.enumValue = cbp.genderCode
cbe.enumType = "sex"
AND cbp.villageCode = #{villageCode}
AND cbp.isLeave = 0 GROUP BY enumName ORDER BY cbe.enumValue;

SELECT cbe.enumValue AS "value", cbe.enumName AS "label", cbe.enumType AS "type", COUNT(1) AS "num" FROM cw_base_enum cbe LEFT JOIN cw_base_people cbp ON cbe.enumValue = cbp.peopleType
cbe.enumType = "peopleType"
AND cbp.villageCode = #{villageCode}
AND cbp.isLeave = 0 GROUP BY enumName ORDER BY cbe.enumValue;

SELECT * FROM ( SELECT cbe.enumValue AS "value", cbe.enumName AS "label", cbe.enumType AS "type", IFNULL(SUM(cbp.num),0) AS "num" FROM cw_base_enum cbe LEFT JOIN ( SELECT COUNT(id) AS 'num',c.label FROM ( select a.id, substring_index(substring_index(a.label,',',b.help_topic_id+1),',',-1) AS 'label' from cw_base_people a join mysql.help_topic b on b.help_topic_id < (length(a.label) - length(replace(a.label,',',''))+1)
a.villageCode = #{villageCode}
AND a.isLeave = 0
) c WHERE c.label!='' GROUP BY c.label ) cbp ON cbe.enumValue = cbp.label WHERE cbe.enumType ='label' AND cbp.num != '' GROUP BY cbp.label ORDER BY num DESC, cbe.enumValue LIMIT 0,10 ) cbpe ORDER BY value

SELECT COUNT(age) FROM ( SELECT TIMESTAMPDIFF( YEAR, str_to_date(birthDate, '%Y-%m-%d'), CURDATE() ) AS 'age', villageCode FROM cw_base_people WHERE isLeave = 0 ) a
a.age >= #{minAge}
and a.age <= #{maxAge}
AND a.villageCode = #{villageCode}

SELECT cbp.*, b.buildingName, u.unitName, h.floor, h.houseNo, h.address AS houseAddress FROM ( SELECT *, TIMESTAMPDIFF( YEAR, str_to_date(birthDate, '%Y-%m-%d'), CURDATE() ) AS 'age' FROM cw_base_people ) cbp LEFT JOIN cw_relation_peoplehouse ph ON cbp.credentialNo = ph.credentialNo LEFT JOIN cw_base_house h ON h.houseNo = ph.houseNo LEFT JOIN cw_base_building b ON b.buildingNo = h.buildingNo LEFT JOIN cw_base_unit u ON u.unitNo = h.unitNo
cbp.isLeave= 0 and cbp.delete_flag = 0
and cbp.genderCode =#{genderCode}
and cbp.peopleType =#{peopleType}
and cbp.age >#{minAge}
and cbp.age <#{maxAge}
and ${sqlMap.labelSql}
AND cbp.peopleName LIKE CONCAT('%', #{peopleName}, '%')
AND cbp.genderCode = #{genderCode}
AND cbp.nation LIKE CONCAT('%', #{nation}, '%')
AND cbp.nationCode = #{nationCode}
AND cbp.credentialNo LIKE CONCAT('%', #{credentialNo}, '%')
AND cbp.phoneNo LIKE CONCAT('%', #{phoneNo}, '%')
AND cbp.villageCode = #{villageCode}
AND cbp.residenceDetailAddres LIKE CONCAT('%',#{residenceDetailAddres},'%')
AND FIND_IN_SET(cbp.villageCode,#{sqlMap.dsf})
ORDER BY cbp.updateTime DESC

mapper.update:
UPDATE cw_base_people
SET
  isLeave = #{isLeave},
  leaveTime = #{leaveTime}
WHERE
	peopleId =#{peopleId}
