mapper.select:
SELECT t1.*, t2.lon, t2.lat, t2.lon AS lng FROM cw_vehicle_record t1 LEFT JOIN cw_vehicle_tollgate t2 ON t1.tollgateID = t2.tollgateID
t1.inOutTime >= #{inOutTimeB}
and t1.inOutTime <=#{inOutTimeE}
and t1.plateNumber =#{plateNumber}
ORDER BY t1.inOutTime

SELECT
	COUNT(id)
FROM
	cw_vehicle_record
WHERE
	    villageCode = #{villageCode}
	AND inOutTime <= SYSDATE()
	AND inOutTime >= #{beginTime}
	AND inOutType = #{inOutType}

SELECT a.id, a.villageCode, a.plateNumber, a.inOutTime, a.inOutType, a.channelName, a.plateNoPicUrl, b.registerName, b.credentialNo, c.residenceDetailAddres, a.platePicUrl FROM cw_vehicle_record a FORCE INDEX(idx_vehiclerecord) LEFT JOIN cw_base_vehicle b FORCE INDEX(idx_vehicle) ON a.plateNumber = b.plateNo AND b.isLeave = 0 LEFT JOIN cw_base_people c FORCE INDEX(idx_people) ON c.credentialNo = b.credentialNo
and a.inOutTime >#{beginTime}
and a.inOutTime <#{endTime}
AND a.plateNumber LIKE CONCAT('%', #{plateNumber}, '%')
AND a.channelName LIKE CONCAT('%', #{channelName}, '%')
AND a.inOutType = #{inOutType}
AND FIND_IN_SET(a.villageCode, #{villageCodes})
ORDER BY a.inOutTime DESC
