mapper.select:
SELECT t3.villageCode, t3.plateNumber, t2.* FROM ( SELECT max(id) AS id FROM cw_vehicle_discovery_detail
discoveryId = #{discoveryId}
) t1 LEFT JOIN cw_vehicle_discovery_detail t2 ON t1.id = t2.id LEFT JOIN cw_vehicle_discovery t3 ON t2.discoveryId = t3.id

mapper.update:
UPDATE cw_vehicle_record


		 withState = #{withState},


		leaveState = #{leaveState},


		retenState = #{retenState}


WHERE

	villageCode = #{villageCode}


	AND plateNumber = #{plateNumber}


	AND inOutTime BETWEEN #{dayBegin} AND #{dayEnd}


	AND inOutTime = #{inOutTime}
