mapper.select:
SELECT d.id, v.villageCode, v.villageName, d.platePicUrl, d.days AS totalDays, d.`status`, d.inOutTime, d.plateNoPicUrl, d.plateNumber, d.remark, DATE_FORMAT(t2.createTime,"%Y-%m-%d") AS alarmTime, t2.dayBegin, t2.dayEnd FROM ( SELECT o.* FROM ( SELECT MAX(id) AS id FROM cw_vehicle_discovery_detail detail
detail.createTime BETWEEN #{beginTime} AND #{endTime}
GROUP BY discoveryId ) dd LEFT JOIN cw_vehicle_discovery_detail o ON o.id = dd.id ) t2 LEFT JOIN cw_vehicle_discovery d ON d.id = t2.discoveryId LEFT JOIN cw_base_village v ON d.villageCode = v.villageCode LEFT JOIN cw_base_vehicle vehicle ON d.vehicleId = vehicle.id
AND d.`status` = 1
AND d.`status` != 1
AND d.plateNumber LIKE CONCAT('%',#{plateNumber},'%')
AND d.`status` = #{status}
AND d.villageCode = #{villageCode}
AND FIND_IN_SET(d.villageCode,#{villageCodes})
AND ${sqlString}
ORDER BY d.`status`, t2.createTime DESC, d.inOutTime DESC

SELECT IFNULL(COUNT(1), 0) FROM ( SELECT count(1) FROM cw_vehicle_record r
r.inOutTime BETWEEN #{beginTime} AND #{endTime}
AND r.plateNumber = #{plateNumber}
AND r.villageCode = #{villageCode}
GROUP BY DATE_FORMAT(r.inOutTime, "%Y-%m-%d") ) t

SELECT
	r.plateNoPicUrl, r.platePicUrl, r.inOutTime, t1.tollgateName AS channelName
FROM
	cw_vehicle_record r
LEFT JOIN cw_vehicle_tollgate t1 ON r.villageCode = t1.villageCode AND r.tollgateID = t1.tollgateID


		r.inOutTime BETWEEN #{beginTime} AND #{endTime}


		AND r.plateNumber = #{plateNumber}


		AND r.villageCode = #{villageCode}


ORDER BY r.inOutTime DESC

mapper.update:
UPDATE cw_vehicle_discovery d


		STATUS = #{status},


		vehicleId = #{id},

	remark = #{remark}



		id = #{handleId}

mapper.select:
SELECT t4.id, t4.days, t5.villageName, t4.platePicUrl, t4.plateNoPicUrl, t4.plateNumber, t4.`status`, DATE_FORMAT(t3.createTime,"%Y-%m-%d") AS dayBegin, t4.remark, t5.villageCode, t4.inOutTime, t6.tollgateName AS channelName FROM (SELECT t2.* FROM (SELECT MAX(id) AS id FROM cw_vehicle_leave_detail detail
detail.createTime BETWEEN #{beginTime} AND #{endTime}
GROUP BY leaveId) t1 LEFT JOIN cw_vehicle_leave_detail t2 ON t1.id = t2.id ) t3 LEFT JOIN cw_vehicle_leave t4 ON t3.leaveId = t4.id LEFT JOIN cw_base_village t5 ON t4.villageCode = t5.villageCode LEFT JOIN cw_vehicle_tollgate t6 ON t4.villageCode = t6.villageCode AND t4.tollgateID = t6.tollgateID
AND t4.`status` = 1
AND t4.`status` != 1
t4.villageCode = #{villageCode}
AND FIND_IN_SET(t4.villageCode,#{villageCodes})
AND t4.plateNumber LIKE CONCAT('%',#{plateNumber},'%')
AND t4.`status` = #{status}
AND ${sqlString}
ORDER BY t4.`status`, t3.createTime DESC, t4.inOutTime DESC

SELECT t4.id, t4.days, t5.villageName, t4.platePicUrl, t4.plateNoPicUrl, t4.plateNumber, t4.`status`, DATE_FORMAT(t3.createTime,"%Y-%m-%d") AS dayBegin, t4.remark, t4.inOutTime, t4.villageCode, t6.tollgateName AS channelName FROM ( SELECT t2.* FROM ( SELECT MAX(id) AS id FROM cw_vehicle_retation_detail detail
detail.createTime BETWEEN #{beginTime} AND #{endTime}
GROUP BY retationId ) t1 LEFT JOIN cw_vehicle_retation_detail t2 ON t1.id = t2.id ) t3 LEFT JOIN cw_vehicle_retention t4 ON t3.retationId = t4.id LEFT JOIN cw_base_village t5 ON t4.villageCode = t5.villageCode LEFT JOIN cw_vehicle_tollgate t6 ON t4.villageCode = t6.villageCode AND t4.tollgateID = t6.tollgateID
AND t4.`status` = 1
AND t4.`status` != 1
t4.villageCode = #{villageCode}
AND FIND_IN_SET(t4.villageCode,#{villageCodes})
AND t4.plateNumber LIKE CONCAT('%',#{plateNumber},'%')
AND t4.`status` = #{status}
AND ${sqlString}
ORDER BY t4.`status`, t3.createTime DESC, t4.inOutTime DESC

SELECT * FROM cw_base_vehicle

	villageCode = #{villageCode}
	AND plateNo = #{plateNo}

SELECT
	*
FROM
	cw_vehicle_record
WHERE

		villageCode = #{villageCode}


		AND plateNumber = #{plateNumber}


		AND inOutType = #{inOutType}

ORDER BY
	inOutTime DESC
