mapper.select:
SELECT t1.id, t1.villageCode, t1.tollgateID, t1.cameraId, t1.recordId, t1.plateType, t1.carType, t1.plateColor, t1.plateNumber, t1.inOutTime, t1.inOutType, t1.platePicUrl, t1.plateNoPicUrl, t1.createTime, t1.updateTime, t1.withState, t2.tollgateName AS 'channelName', t2.lon, t2.lat, t2.lon AS lng FROM cw_vehicle_record t1 LEFT JOIN cw_vehicle_tollgate t2 ON t1.tollgateID = t2.tollgateID
t2.tollgateID IS NOT NULL AND t2.lon IS NOT NULL AND t2.lat IS NOT NULL
AND t1.inOutTime >= #{inOutTimeB}
AND t1.inOutTime <=#{inOutTimeE}
AND t1.plateNumber =#{plateNumber}
AND FIND_IN_SET(t1.villageCode,#{villageCode})
ORDER BY t1.inOutTime DESC
